// n8nì—ì„œ HTTP Request ì‘ë‹µì€ ë³´í†µ $json.body ë˜ëŠ” $json.dataì— ìˆìŒ
// XMLì´ ë¬¸ìì—´ë¡œ ë“¤ì–´ì˜¤ëŠ” ê²½ìš°ì™€ ê°ì²´ë¡œ ë“¤ì–´ì˜¤ëŠ” ê²½ìš° ëª¨ë‘ ì²˜ë¦¬
let xmlString = '';

if (typeof $json.body === 'string') {
  // bodyê°€ ë¬¸ìì—´ì¸ ê²½ìš°
  xmlString = $json.body;
} else if ($json.body && typeof $json.body === 'object') {
  // bodyê°€ ê°ì²´ì¸ ê²½ìš°, JSON.stringifyë¡œ ë³€í™˜ í›„ ë‹¤ì‹œ íŒŒì‹±
  xmlString = $json.body.data || $json.body.xml || JSON.stringify($json.body);
} else if (typeof $json.data === 'string') {
  // data í•„ë“œì— ë¬¸ìì—´ë¡œ ìˆëŠ” ê²½ìš°
  xmlString = $json.data;
} else if (typeof $json === 'string') {
  // $json ìì²´ê°€ ë¬¸ìì—´ì¸ ê²½ìš°
  xmlString = $json;
} else {
  // ê°ì²´ì¸ ê²½ìš° ë¬¸ìì—´ë¡œ ë³€í™˜ ì‹œë„
  xmlString = $json.xml || JSON.stringify($json);
}

// ì •ê·œì‹ì„ ì‚¬ìš©í•´ì„œ ëª¨ë“  <entry>...</entry> ë¸”ë¡ ì¶”ì¶œ
// TODO: í–¥í›„ XML íŒŒì„œ ë¼ì´ë¸ŒëŸ¬ë¦¬ë¡œ êµì²´ (ì˜ˆ: xml2js, DOMParser)
const entryRegex = /<entry[^>]*>([\s\S]*?)<\/entry>/gi;
const entries = [];
let match;

while ((match = entryRegex.exec(xmlString)) !== null) {
  entries.push(match[1]); // entry íƒœê·¸ ì•ˆì˜ ë‚´ìš©
}

const posts = [];

// ê° entryì—ì„œ titleê³¼ link ì¶”ì¶œ
for (let i = 0; i < entries.length; i++) {
  const entryContent = entries[i];

  // title ì¶”ì¶œ: <title>...</title> ë˜ëŠ” <title type="text">...</title>
  const titleMatch = entryContent.match(/<title[^>]*>(.*?)<\/title>/is);
  const title = titleMatch ? titleMatch[1]
    .replace(/<!\[CDATA\[(.*?)\]\]>/gi, '$1') // CDATA ì œê±°
    .trim() : '';

  // link ì¶”ì¶œ: href ì†ì„± ìš°ì„ , ì—†ìœ¼ë©´ <link>...</link> ë‚´ìš©
  let link = '';
  const linkHrefMatch = entryContent.match(/<link[^>]*href=["']([^"']+)["'][^>]*>/i);
  if (linkHrefMatch) {
    link = linkHrefMatch[1].trim();
  } else {
    const linkContentMatch = entryContent.match(/<link[^>]*>(.*?)<\/link>/is);
    if (linkContentMatch) {
      link = linkContentMatch[1]
        .replace(/<!\[CDATA\[(.*?)\]\]>/gi, '$1') // CDATA ì œê±°
        .trim();
    }
  }

  if (title && link) {
    posts.push({ title, link });
  }
}

// '1. [ì œëª©] - [ë§í¬] \n 2. [ì œëª©] - [ë§í¬] ...' í˜•ì‹ìœ¼ë¡œ í•©ì¹˜ê¸°
const formattedPosts = posts.map((post, index) => {
  return `${index + 1}. ${post.title} - ${post.link}`;
}).join('\n');

// ê²°ê³¼ë¥¼ JSON í˜•íƒœë¡œ ë¦¬í„´
return {
  json: {
    reddit_posts: formattedPosts
  }
};
