const inputItems = $input.all();

function getXmlString(itemJson) {
  if (typeof itemJson?.body === 'string') return itemJson.body;
  if (itemJson?.body && typeof itemJson.body === 'object') {
    return itemJson.body.data || itemJson.body.xml || JSON.stringify(itemJson.body);
  }
  if (typeof itemJson?.data === 'string') return itemJson.data;
  if (typeof itemJson === 'string') return itemJson;
  return itemJson?.xml || JSON.stringify(itemJson || {});
}

function decodeHtml(html) {
  if (!html) return '';
  return String(html)
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'");
}

function parseEntries(xmlString, source) {
  const entryRegex = /<entry[^>]*>([\s\S]*?)<\/entry>/gi;
  const entries = [];
  let match;

  while ((match = entryRegex.exec(xmlString)) !== null) {
    entries.push(match[1]);
  }

  const parsed = [];
  for (const entryContent of entries) {
    const titleMatch = entryContent.match(/<title[^>]*>(.*?)<\/title>/is);
    let title = titleMatch
      ? titleMatch[1].replace(/<!\[CDATA\[(.*?)\]\]>/gi, '$1').trim()
      : '';
    title = decodeHtml(title);

    let link = '';
    const linkHrefMatch = entryContent.match(/<link[^>]*href=[\"']([^\"']+)[\"'][^>]*>/i);
    if (linkHrefMatch) {
      link = linkHrefMatch[1].trim();
    } else {
      const linkContentMatch = entryContent.match(/<link[^>]*>(.*?)<\/link>/is);
      if (linkContentMatch) {
        link = linkContentMatch[1]
          .replace(/<!\[CDATA\[(.*?)\]\]>/gi, '$1')
          .trim();
      }
    }

    if (title && link) {
      parsed.push({ title, link, source });
    }
  }

  return parsed;
}

const dedupe = new Map();
for (const item of inputItems) {
  const source = item?.json?.source || 'unknown';
  const xmlString = getXmlString(item?.json || {});
  const posts = parseEntries(xmlString, source);

  for (const post of posts) {
    if (!dedupe.has(post.link)) {
      dedupe.set(post.link, post);
    }
  }
}

const uniquePosts = Array.from(dedupe.values());
const formattedPosts = uniquePosts.length > 0
  ? uniquePosts.map((post, index) => `${index + 1}. [${post.source}] ${post.title} - ${post.link}`).join('\n')
  : 'No relevant K-Pop challenge posts found lately.';

return [
  {
    json: {
      reddit_posts: formattedPosts,
      post_count: uniquePosts.length,
      should_send: uniquePosts.length > 0 ? 'yes' : 'no',
      checked_sources: inputItems.length,
    },
  },
];

const userAgents = [
  'KPopTrendHunter/1.0 (+https://n8n.io)',
  'Mozilla/5.0 (compatible; KPopTrendHunter/1.0)',
  'RedditTrendCollector/1.0',
];

const targets = [
  { subreddit: 'kpop', query: 'challenge' },
  { subreddit: 'kpopthoughts', query: 'challenge' },
  { subreddit: 'kpophelp', query: 'challenge' },
];

const offset = Math.floor(Math.random() * userAgents.length);

return targets.map((target, index) => {
  const ua = userAgents[(offset + index) % userAgents.length];
  const rssUrl = `https://www.reddit.com/r/${target.subreddit}/search.rss?q=${encodeURIComponent(target.query)}&restrict_sr=on&sort=new&t=week`;

  return {
    json: {
      source: `${target.subreddit}:${target.query}`,
      rss_url: rssUrl,
      user_agent: ua,
    },
  };
});
